import React from 'react';
import "./styles/testForm.css";

class Form extends React.Component{
    handleChange= e =>{
        this.setState({
            nombre: e.target.value
        })
    }
    handleClick= e =>{
        console.log("boton")
    }

    render(){
        return(
            <div>
            <h5>
                Hola, bienvenido, sabemos que quieres viajar en un X, por favor
                diligencia el siguiente formulario:
            </h5>

            <form>  
                <div className="form-group">
                    <label>Nombre completo</label>
                    <input onChange={this.handleChange}  className="form-control" type="text" name="nombre" required="required"/>
                    <label>Email</label>
                    <input className="form-control" type="email" name="email" required="required"/>
                    <label>Celular</label>
                    <input className="form-control" type="number" name="celular" required="required"/>
                    <label>Edad</label>
                    <input className="form-control" min="18" max="99" type="number" name="edad" required="required"/>
                </div>
                <button type="button" onClick={this.handleClick} className="btn btn-primary">Save</button>
            </form>
            </div>


        )
    }
}

export default Form;
