import React from 'react';
import "./styles/testForm.css";

class Form extends React.Component{
    state={};
    handleChange= e =>{
        this.setState({
            [e.target.name]: e.target.value
        })
    }
    handleClick= e =>{
        console.log("boton")
    }
    handleSubmit=e=>{
        console.log(this.state)
    }

    render(){
        return(
            <div>
            <h5>
                Hola, bienvenido, sabemos que quieres viajar en un X, por favor
                diligencia el siguiente formulario:
            </h5>

            <form onSubmit={this.handleSubmit}>  
                <div className="form-group">
                    <label>Nombre completo</label>
                    <input 
                        onChange={this.handleChange}  
                        className="form-control" 
                        type="text" 
                        name="nombre" 
                        value={this.state.nombre}
                        required="required"/>
                    <label>Email</label>
                    <input 
                        onChange={this.handleChange} 
                        className="form-control" 
                        type="email" 
                        name="email" 
                        value={this.state.email}
                        required="required"/>
                    <label>Celular</label>
                    <input 
                        onChange={this.handleChange} 
                        className="form-control" 
                        type="number" 
                        name="celular" 
                        value={this.state.celular}
                        required="required"/>
                    <label>Edad</label>
                    <input 
                        onChange={this.handleChange} 
                        className="form-control" 
                        min="18" 
                        max="100" 
                        type="number" 
                        name="edad" 
                        value={this.state.edad}
                        required="required"/>
                </div>
                <button type="button" onClick={this.handleClick} className="btn btn-primary">Save</button>
            </form>
            </div>


        )
    }
}

export default Form;
